// http://api.jquery.com/jQuery.ajax/
// http://api.jquery.com/jquery.ajax/
// http://api.jquery.com/jquery.get/


$(document).ready(function(){

	$( "#formmarea" ).submit(function( event ) {

        console.log("submit");
        /* Stop form from submitting normally */
        event.preventDefault();

        $.ajax({
           type: "GET",
           url: "/buscarmarea",
           dataType: "json",
           success: function(data){
              console.log(data);
              // https://developer.mozilla.org/en-US/docs/Web/API/document.getElementById
              document.getElementById("listademareasDiv").innerHTML = data.dia;
              var mareas = "<p> Dia:"+data.dia+"</p><p>Pleamar:<br>"+data.pleamar[0]+";"+data.pleamar[1]+"</p>"+"</p><p>Bajamar:<br>"+data.bajamar[0]+";"+data.bajamar[1]+"</p>"
              $( "#listademareasDiv" ).html(mareas);
           }
        });

        // other options using $.get
        // http://api.jquery.com/jquery.get/

    });


	$( "#formlistamareas" ).submit(function( event ) {

        console.log("submit");
        /* Stop form from submitting normally */
        event.preventDefault();

        $.ajax({
           type: "GET",
           url: "/mareas",
           dataType: "json",
           success: function(data){
              console.log(data);
              for(i=0;i<data.length;i++){
              	// https://developer.mozilla.org/en-US/docs/Web/API/document.getElementById
              	document.getElementById("listademareasDiv").innerHTML = data[i].dia;
              	var mareas = "<p> Dia:"+data[i].dia+"</p><p>Pleamar:<br>"+data[i].pleamar[0]+";"+data.pleamar[1]+"</p>"+"</p><p>Bajamar:<br>"+data[i].bajamar[0]+";"+data[i].bajamar[1]+"</p>"
              	$( "#listademareasDiv" ).html(mareas);
              }
              
           }
        });

        // other options using $.get
        // http://api.jquery.com/jquery.get/

    });
});